Join Grades

exam_all &lt;- inner_join(grades1, schedule, by = "Points") |&gt;
  select(-assessment) |&gt;
  rename("Points_exam" = Points,
         "Grade_exam" = Grade)


essay_all &lt;- inner_join(grades2, schedule, by = "Points") |&gt;
  select(-assessment) |&gt;
  rename("Points_essay" = Points,
         "Grade_essay" = Grade)
